    if (x < (Q1 - 1.5 * IQR)) or ((Q3 + 1.5 * IQR) < x):
        if x not in outliers_Lng:
            outliers_Lng.append(x)

#print (sorted(outliers_Lng))
for outlier in outliers_Lng:
    df = df[df['Lng']!=outlier]
print ("DATA", df.shape)

