def getZscoreOutlier(df,col):
    out = []
    m = np.mean(df[col])
    sd = np.std(df[col])

    for i in df[col]: 
        z = (i-m)/sd
        if np.abs(z) > 3: 
            out.append(i)

    print("Outliers:",out)
    print("min",np.min(out))
    return np.min(out)
