@jit(nopython=True, fastmath=True)
def score(preferences, occupancy):
    # Accountancy cost     
    score = c2[occupancy[-1], occupancy[-1]]
    for i in range(occupancy.size - 2, -1, -1):
        score += c2[occupancy[i], occupancy[i + 1]]
    # Preference cost    
