# Using the following function to convert the 'Value' feature from 'object' dtype to 'int' dtype
def get_value(x):
    float_value = float(x[1:-1])
    if x[-1] == 'M':
        float_value = float_value * 1000000
    elif x[-1] == 'K':
        float_value = float_value * 1000
    else:
        float_value == 0
    return float_value    
data.Value = data.Value.apply(get_value)

