# Using the following function to convert the 'Wage' feature from 'object' dtype to 'int' dtype
def get_value(x):
    float_value = int(x[1:-1])
    if x[-1] == 'K':
        int_value = float_value * 1000
    else:
        int_value == 0
    return int_value    
data.Wage = data.Wage.apply(get_value)
