def topic_class(x):
    if(x['Computer Science']):
        return 0
    if(x['Physics']):
        return 1
    if(x['Mathematics']):
        return 2
    if(x['Statistics']):
        return 3
    if(x['Quantitative Biology']):
        return 4
    if(x['Quantitative Finance']):
        return 5

df['class']=df.apply(topic_class,axis=1)

