class HFlipWrapper(nn.Module):
    def __init__(self, model, flip_dim=(-1,)):
        super(HFlipWrapper, self).__init__()
        self.model = model
        self.flip_dim = flip_dim

    def forward(self, x):
        with torch.no_grad():
            xf = torch.flip(x, self.flip_dim)
        pred = self.model(torch.stack([x, xf]))
        return pred

