# Fungsi untuk menyamakan satuan menjadi km/kg
# Menggunakan asumsi 1 liter minyak = 0,8 kg
def samakan_satuan(mileage):
    if pd.notna(mileage):
        satuan= mileage.split(" ")[1]
        jarak = float(mileage.split(" ")[0])
        if satuan == "kmpl":
            jarak = jarak / 0.8
            return jarak
        return jarak

