import csv

label_count = {}
with open('../input/inclusive-images-challenge/tuning_labels.csv') as f:
    csv_rdr = csv.reader(f)
    for row in csv_rdr:
        inst_labels = row[1].split()
        label_count = {**label_count, **{l: label_count.get(l, 0) + 1 for l in inst_labels}}

