def levenshtein_distance(a, b)-> str:

    d = np.zeros((len(a), len(b)))

    for i in range(len(a)):
        d[i, 0] = i

    for j in range(len(b)):
        d[0, j] = j

    for j in range(len(b)):
        for i in range(len(a)):
            if a[i] == b[j]:
                cost = 0
            else:
                cost = 1
            d[i, j] = min(d[i-1, j]+1, d[i, j-1] + 1,d[i-1, j-1] + cost)
    return d[len(a)-1, len(b)-1]
