def save_to_zip(out_path):
    # ZIP & rm -rf (remove) folder

    if 'test' in out_path:
        !zip -r test.zip test
        !rm -rf test

    if 'train_1' in out_path:
        !zip -r train_1.zip train_1
        !rm -rf train_1

    if 'train_2' in out_path:
        !zip -r train_2.zip train_2
        !rm -rf train_2
