def city_select(adr):
    if adr == 'Выездная Торговля':
        return 'Выезд'
    if adr == 'Интернет-магазин ЧС' or adr == 'Цифровой склад 1С-Онлайн':
        return 'Онлайн'
    else:
        return adr.split()[0]

