def get_most_cited_paper_title(paper_list):
    df_score = pd.read_csv('/kaggle/input/covid-challenge/df_paper_score.csv')

    df_part = df_score[df_score['id'].isin(paper_list)]
    if len(df_part) > 0:
        title = df_part.sort_values(by='score').iloc[-1, 1]
    else:
        title = ''

    return title

