class_to_id = { 
    'negative': 0,
    'typical': 1,
    'indeterminate': 2,
    'atypical': 3}
id_to_class  = {v:k for k, v in class_to_id.items()}

def get_study_prediction_string(preds, threshold=0):
    string = ''
    for idx in range(4):
        conf =  preds[idx]
        if conf>threshold:
            string+=f'{id_to_class[idx]} {conf:0.2f} 0 0 1 1 '
    string = string.strip()
    return string
