def mean_reward(rewards):
    return sum(r[0] for r in rewards) / float(len(rewards))

# Run multiple episodes to estimate its performance.
print("Greedy vs Random Agent:", mean_reward(evaluate("hungry_geese", [agent, "random"], num_episodes=10)))
print("Greedy vs Greedy:", mean_reward(evaluate("hungry_geese", [agent, agent], num_episodes=10)))
