# Prediction
def predict(w,b,x_test):
    #calculate z
    z=sigmoid(np.dot(w.T,x_test) + b)    
    #fill y_prediction with zeros
    y_prediction=np.zeros((1,x_test.shape[1]))
    # if z bigger than 0.5, y_head=1 else 0
    for i in range(z.shape[1]):
        if z[0,i]<=0.5:
            y_prediction[0,i]=0
        else:
            y_prediction[0,i]=1
    return y_prediction
