def string_extract(x):
    X=x.split()

    if(len(X)==2):
        return 1
    if(X[0]=='<'):
        return 0
    if(X[0]=='>'):
        return 2


df_train['Vehicle_Age_n']=df_train['Vehicle_Age'].apply(lambda x:string_extract(x))
