class Perceptron:
    def __init__ (self):
        self.w = None
        self.b = None
        self.device = None
        self.random_inits = {'normal':np.random.normal,'uniform':np.random.uniform}

    def model(self, x):
        dot_product = np.dot(self.w, x)
        return 1 if (dot_product >= self.b) else 0

    def predict(self, X):
        Y = []
        for x in X:
            result = self.model(x)
            Y.append(result)
        return np.array(Y)

    def fit(self, X, Y, epochs = 1, lr = 1,seeds=(1,1),init='normal',random_inits=True):
        if X.shape[0] != Y.shape[0]:
            print('X and Y have different shapes!',X.shape[0],'!=',Y.shape[0])
            return
        if random_inits:
            np.random.seed(seeds[0])
            self.w = self.random_inits[init](size=X.shape[1])
            np.random.seed(seeds[1])
            self.b = self.random_inits[init]()
        else:
            self.w = np.ones(X.shape[1])
            self.b = 0

        accuracy = {}
        max_accuracy = 0
        wt_matrix = []

        for i in range(epochs):
            for j in range(X.shape[0]):
                x,y = X[j],Y[j]
                y_pred = self.model(x)
                if y == 1 and y_pred == 0:
                    self.w = self.w + lr * x
                    self.b = self.b - lr * 1
                elif y == 0 and y_pred == 1:
                    self.w = self.w - lr * x
                    self.b = self.b + lr * 1

            accuracy[i] = accuracy_score(self.predict(X), Y)

            if (accuracy[i] > max_accuracy):
                max_accuracy = accuracy[i]
                chkptw = self.w
                chkptb = self.b

        self.w = chkptw
        self.b = chkptb

        print(max_accuracy)

        plt.plot(accuracy.values())
        plt.ylim([0, 1])
        plt.show()

        return max_accuracy

