def unit_to_float(x):
    if type(x) == float or type(x) == int:
        return x
    if 'K' in x or 'k' in x: 
        if len(x) > 1:
            return float(x.replace('K', '')) * 1000
        return 1000.0
    if 'M' in x or 'm' in x:
        if len(x) > 1:
            return float(x.replace('M', '')) * 1000000
        return 1000000.0
    if 'B' in x or 'b' in x:
        return float(x.replace('B', '')) * 1000000000
    return 0.0
