def combine(x):
    if not(pd.isna(x['Blogs_media_sources']) and pd.isna(x['Coursera_platform'])):
        return 'BLOGS/MEDIA/COURSES'
    elif pd.isna(x['Blogs_media_sources']):
        return 'BLOGS/MEDIA'
    elif pd.isna(x['Coursera_platform']):
        return 'COURSES'
    else:
        return 'N/A'
