stri_month = data[['Code', 'Country', 'Month', 'Population', 'Cases', 'GDP_Cap', 'HDI', 'Stringency_Index']].groupby(['Country', 'Month', 'Stringency_Index', 'Population', 'GDP_Cap', 'HDI', 'Code'], as_index=False).sum()
