# Create a function to remove special characters
def remove_special_characters(text):
    pat = r'[^a-zA-z0-9]' 
    return re.sub(pat, ' ', text)

# lets apply this function
df['text-en'] = df.apply(lambda x: remove_special_characters(x['text-en']), axis = 1)
