def create_day(x):
    return int(x.split("/")[0])
def create_month(x):
    return int(x.split("/")[1])
def create_year(x):
    return int(x.split("/")[2])

new_data['Day'] = new_data['Date'].apply(create_day)
new_data['Month'] = new_data['Date'].apply(create_month)
new_data['Year'] = new_data['Date'].apply(create_year)

