def return_columns(top):
    indicators = ['st', 'nd', 'rd']

    columns = []
    for ind in np.arange(top):
        try:
            columns.append('{}{} Important Feature'.format(ind+1, indicators[ind]))
        except:
            columns.append('{}th Important Feature'.format(ind+1))
    return columns
