# checks if any of the keywords appear in text
def is_match(text, keywords):

    return any(f'{keyterm} ' in text.lower() for keyterm in keywords)
# loading files from json
def load_json(filename):
    '''Load json file.'''
    with open(filename, 'r') as f:
        return json.load(f)

