def regioncolors(counter):
    if counter['Rating text'] in ['Excellent', 'Very Good']:
        return 'green'
    elif counter['Rating text'] == 'Good':
        return 'Yellow'
    elif counter['Rating text'] == 'Poor':
        return 'red'
    else:
        return 'blue'
rt_ind_df["Rating colors"] = rt_ind_df.apply(regioncolors, axis=1)
