from pprint import pprint

news_list = [news1, news2, news3, news4, news5]
word_Dict = {}
for i, news in enumerate(news_list):
    news = news.split()
    temp = {}

    for word in news:
        temp[word] = 0
    for word in news:
        temp[word] = temp[word] + 1

    word_Dict[i] = temp
pprint(word_Dict)
