def manage_no_result(matches):
    for idx in matches[matches['result'].isna()].index:
        matches.loc[idx, 'winner'] = 'no result'   
        matches.loc[idx, 'result'] = 'no result'
        matches.loc[idx, 'result_margin'] = 'no result'

