def change_neutral_venue(matches, change_neutral_venue_ids):
    for idx in matches.index:
        if idx in change_neutral_venue_ids:
            matches.loc[idx, 'neutral_venue'] = 1 
        if matches.loc[idx, 'date'][0:4] == '2020':
            matches.loc[idx, 'neutral_venue'] = 1

