def normalize(train, test, col, type_='StandardScaler'):

    if type_ == 'StandardScaler':
        clf = StandardScaler()
    elif type_ == 'MinMaxScaler':
        clf = MinMaxScaler()
    elif type_ == 'RobustScaler':
        clf = RobustScaler()
    else:
        print('Error type in!')

    train[col] = clf.fit_transform(train[col])
    test[col] = clf.transform(test[col])

    return train, test

