age_grp = []
for age in train['Age']:
    if(math.isnan(age)):
        age_grp.append('Not Defined')
    elif(age<14):
        age_grp.append('Child')
    else:
        age_grp.append('Adult')

train['Age Group'] = age_grp
age_survived = train.groupby(['Age Group','Survived']).count()['PassengerId']
print(age_survived)
