def Ticket_Categorizer():
    a = int(input("What was your maximum speed?"))
    b = int(input("Is it your birthday? 0:No  1:Yes"))
    if b==0 :
        if a>0 and a<60:
            return("No Ticket")
        elif a>80:
            return("Big Ticket")
        elif a>=60 and a<=80:
            return("Small ticket")
        else:
            return("Invalid input")
    elif b==1:
        if a>0 and a<65:
            return("No Ticket")
        elif a>85:
            return("Big Ticket")
        elif a>=65 and a<=85:
            return("Small ticket")
        else:
            return("Invalid input")
    else:
        return("Invalid input")


Ticket_Categorizer()
