# Bag of words
df['final_keywords'] = ''
columns = df.columns
for index, row in df.iterrows():
    words = ''
    for col in columns:
        if col != 'director':
            words = words + ' '.join(row[col])+ ' '
        else:
            words = words + row[col]+ ' '
    row['final_keywords'] = words

df.drop(columns = [col for col in df.columns if col!= 'final_keywords'], inplace = True)
