def should_hit(player_total, dealer_card_val, player_aces):
    """Return True if the player should hit (request another card) given the current game
    state, or False if the player should stay. player_aces is the number of aces the player has.
    """
    # for 'hard' hand
    if player_aces == 0:
        if player_total >= 17:
            return False    

        # stand if dealer's cards are weak
        if player_total > 12 and dealer_card_val < 7: 
            return False

        return True

    # for 'soft' hand    
    # Ace in player_total means the largest total that can be made without exceeding 21
    else:
        if player_total >= 19:
            return False    
        if player_total >= 18 and dealer_card_val in [9, 10, 11]:
            return False

    return True


