i=str(input('请输入你的名字'))
lst=['n','x','s']
line=[]
i_list=[]
i_list.append(i)
for i in i_list:
    if i not in line:
        if i not in lst:
            line.append(i)
            print('您的名字已成功进入序列中')
        else:
            if i == 'n':
                print(line[0])
                line.remove(0)
            elif i =='x':
                print(line)
            else:
                print(len(line))
        i=str(input('请继续输入名字'))
        i_list.append(i)
