def define_grades(perc):
    if perc <= 39:
        return 'F'
    elif perc >= 40 and perc <= 50:
        return 'E'
    elif perc >= 51 and perc <= 60:
        return 'D'
    elif perc >= 61 and perc <= 70:
        return 'C'
    elif perc >= 71 and perc <= 80:
        return 'B'
    elif perc >= 81 and perc <= 90:
        return 'A'
    elif perc >= 91 and perc <= 100:
        return 'A+'
    else:
        return ''
