def fast_df_plot(x):
    sns.set()
    df1 = pd.DataFrame(x.dt.year.value_counts()).reset_index()
    return sns.lineplot(x=df1.iloc[:,0], y=df1.iloc[:,1]).set_title("Original Release Date x Year")

