d = dict()
for row in prediction.itertuples():
    if row.reordered == 1:
        try:
            d[row.order_id] += ' ' + str(row.product_id)
        except:
            d[row.order_id] = str(row.product_id)

for order in prediction.order_id:
    if order not in d:
        d[order] = 'None'

gc.collect()
