#Funtion to categorize the cost for two values
def cost_for_two(value):
    if value < 200:
        return "<200"
    elif value < 500:
        return "200-500"
    elif value < 800:
        return "500-800"
    elif value < 1500:
        return "800-1500"
    elif value <3000:
        return "1500-3000"
    else:
        return ">3000"

