def range_function(col,data,metrics):
    col_s=list(data[col])
    std=metrics[col]["std"]
    col_s=pd.Series(col_s/std)
    return pd.Series(col_s.apply(apply_funct))

def apply_funct(val):
    if val<=0.5:
        return 1
    elif val<=1:
        return 2
    elif val<=1.5:
        return 3
    elif val<=2:
        return 4
    elif val<=2.5:
        return 5
    elif val<=3:
        return 6
    elif val<=3.5:
        return 7
    elif val<=4:
        return 8
    elif val<=4.5:
        return 9
    elif val<=5:
        return 10
    else:
        return 11

