def fashionably_late(arrivals, name):
    """Given an ordered list of arrivals to the party and a name, return whether the guest with that
    name was fashionably late.
    """
    total = len(arrivals)
    candidate_number = arrivals.index(name)+1
    if total%2==0:
        half_value = total/2
    else:
        half_value = (total+1)/2
    return (candidate_number != total and candidate_number > half_value)


