def txt2csv(file_path, pp):
    with open(file_path, 'r') as f:
        f = f.readlines()
        f = np.array(f).reshape(5+pp, -1, order='F')
        return pd.DataFrame({'#':f[0], 'Team Name':f[1+pp], 'Score':f[2+pp], 'Entries':f[3+pp], 'Last':f[4+pp]})

