        playstore.loc[i,'Size'] = playstore.loc[i,'Size'][:-1]
        playstore.loc[i,'Size'] = float(playstore.loc[i,'Size']) / 1000
    elif 'M' in playstore.loc[i,'Size'] :
        playstore.loc[i,'Size'] = playstore.loc[i,'Size'][:-1]
        playstore.loc[i,'Size'] = float(playstore.loc[i,'Size'])

