import PIL
from PIL import UnidentifiedImageError
import glob

imgs_ = glob.glob("../input/is-that-santa-image-classification/is that santa/train/*/*.jpg")

for img in imgs_:
    try:
        img = PIL.Image.open(img)
    except PIL.UnidentifiedImageError:
        print(img)
