def count_uppercase(text):  
    count=0
    for char in text:
        if char.isupper():
            count+=1

    return count

data['count_uppercase'] = data['review'].apply(lambda x: count_uppercase(x))

sns.displot(data=data, x="count_uppercase", hue='sentiment', col="sentiment", bins=20, multiple="dodge", aspect=1.5)

