# This function is to select a pair from the array, one by one and return the maximum LCM among them
def max_lcm(arr):
        arr = list(arr)
        n = len(arr)
        Max = []
        for i in range(n-1):
                for j in range(i+1, n+i):
                        if j<n:
                                a =compute_lcm(arr[i], arr[j])
                                Max.append(a)
        b = tuple(Max)
        return max(b)
