#simplified version of rle2mask() that returns total number of pixels
def pixel_count(rle):

    if np.logical_or(rle==0, rle==''):
        return(np.nan)
    array = np.asarray([int(x) for x in rle.split()])
    lengths = array[1::2]

    return sum(lengths)

