features = ['PC1', 'PC2', 'PC3', 'Price']

for i in features:
    m = PCA_data[f'{i}'].mean()
    s = PCA_data[f'{i}'].std()
    val = [0, 0, 0]
    for j in PCA_data[f'{i}'].values:
        if j > m - s and j < m + s:
            val[0] += 1
        if j > m - 2 * s and j < m + 2 * s:
            val[1] += 1
        if j > m - 3 * s and j < m + 3 * s:    
            val[2] += 1
    print(f'\nPr⁡(µ - 1σ ≤ {i} ≤ µ + 1σ) =', val[0] / 50 / 120)
    print(f'Pr⁡(µ - 2σ ≤ {i} ≤ µ + 2σ) =', val[1] / 50 / 120)
    print(f'Pr⁡(µ - 3σ ≤ {i} ≤ µ + 3σ) =', val[2] / 50 / 120)

print('\n')

