def track(times: int, quantity: int = 3, sides: int = 6) -> {int:int}:

    history = {}

    minimum = quantity
    limit = quantity * sides

    for key in range(minimum, limit+1):
        history[key] = 0

    for t in range(times):
        total = roll(quantity,sides)
        history[total] += 1

    return history

