def time_bin(x):
    if x<120:
        return '0 ~ 2 mintues'
    elif x<180:
        return '2 ~ 3 mintues'
    elif x<300:
        return '3 ~ 5 mintues'
    elif x<600:
        return '5 ~ 10 mintues'

    elif x<900:
        return '10 ~ 15 mintues'
    elif x<1800:
        return '15 ~ 30 mintues'
    elif x<3600:
        return '30 ~ 60 mintues'
    else:
        return '60 ~ mintues'

Survey_JP['TimeCategory']=Survey_JP['Time from Start to Finish (seconds)'].apply(time_bin)

